------------------------------------------------
-- DDL Statements for Table "LCVINBOM  "."MODEL_PART"
------------------------------------------------
 
DROP TABLE "LCVINBOM  "."MODEL_PART";


CREATE TABLE "LCVINBOM  "."MODEL_PART"  (
		  "MODEL_PART_ID" BIGINT NOT NULL GENERATED BY DEFAULT AS IDENTITY (  
		    START WITH +1  
		    INCREMENT BY +1  
		    MINVALUE +1  
		    MAXVALUE +9223372036854775807  
		    NO CYCLE  
		    NO CACHE  
		    NO ORDER ) ,
		  "PRODUCT_SPEC_WILDCARD" CHAR(30 OCTETS) NOT NULL , 
		  "LET_SYSTEM_NAME" VARCHAR(255 OCTETS) NOT NULL , 
		  "DC_PART_NUMBER" CHAR(18 OCTETS) NOT NULL , 
		  "DC_NUMBER" VARCHAR(15 OCTETS) NOT NULL , 
		  "DC_CLASS" VARCHAR(10 OCTETS) NOT NULL , 
		  "REFLASH" INTEGER NOT NULL , 
		  "INTERCHANGEABLE" INTEGER NOT NULL , 
		  "SCRAP_PARTS" INTEGER NOT NULL , 
		  "ACTIVE" INTEGER NOT NULL ,
		  "CREATE_TIMESTAMP" TIMESTAMP , 
		  "UPDATE_TIMESTAMP" TIMESTAMP )   
		 DATA CAPTURE CHANGES  
		 IN "LCVINBOMTBSDAT1" INDEX IN "LCVINBOMTBSIDX1"  
		 ORGANIZE BY ROW; 

COMMENT ON TABLE "LCVINBOM  "."MODEL_PART" IS 'VIN-BOM Rules for updating part number from a design change';


-- DDL Statements for Primary Key on Table "LCVINBOM  "."MODEL_PART"

ALTER TABLE "LCVINBOM  "."MODEL_PART" 
	ADD CONSTRAINT "MODEL_PART_PK" PRIMARY KEY
		("MODEL_PART_ID");

ALTER TABLE "LCVINBOM"."MODEL_PART" 
	ADD CONSTRAINT "MODELPART_IX1" UNIQUE 
		("PRODUCT_SPEC_WILDCARD","LET_SYSTEM_NAME", "DC_PART_NUMBER");

