
------------------------------------------------
-- DDL Statements for Table "VIOS    "."PAPIC1"
------------------------------------------------
 
DROP TABLE "VIOS    "."PAPIC1";


CREATE TABLE "VIOS    "."PAPIC1"  (
		  "MASK_MAPPER_REL_ID" INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY (  
		    START WITH +1  
		    INCREMENT BY +1  
		    MINVALUE -2147483648  
		    MAXVALUE +2147483647  
		    NO CYCLE  
		    NO CACHE  
		    NO ORDER ) , 
		  "MAPPER_MATRIX_ID" INTEGER , 
		  "PID_PART_MASK_ID" INTEGER , 
		  "PLANT_LOC_CODE" CHAR(1 OCTETS) , 
		  "CREATE_TIMESTAMP" TIMESTAMP , 
		  "UPDATE_TIMESTAMP" TIMESTAMP )   
		 DATA CAPTURE CHANGES  
		 IN "VIOSTBSDAT2" INDEX IN "VIOSTBSIDX2"  
		 ORGANIZE BY ROW; 

COMMENT ON TABLE "VIOS    "."PAPIC1" IS 'RMT VIOS PID Color Mapping Table';


-- DDL Statements for Primary Key on Table "VIOS    "."PAPIC1"

ALTER TABLE "VIOS    "."PAPIC1" 
	ADD CONSTRAINT "PAPIC1_PK" PRIMARY KEY
		("MASK_MAPPER_REL_ID");



-- DDL Statements for Indexes on Table "VIOS    "."PAPIC1"

SET SYSIBM.NLS_STRING_UNITS = 'SYSTEM';

CREATE INDEX "VIOS    "."PAPIC_I1" ON "VIOS    "."PAPIC1" 
		("MASK_MAPPER_REL_ID" ASC,
		 "MAPPER_MATRIX_ID" ASC)
		
		COMPRESS NO 
		INCLUDE NULL KEYS ALLOW REVERSE SCANS;






