UPDATE GALADM.GAL214TBX gal214 SET gal214.TRACKING_POINT_FLAG = 1 WHERE gal214.TRACKING_POINT_FLAG < 1 AND EXISTS (SELECT 1 FROM GALADM.GAL489TBX gal489a WHERE gal489a.COMPONENT_ID = gal214.PROCESS_POINT_ID AND gal489a.PROPERTY_KEY = 'PANELS' AND gal489a.PROPERTY_VALUE LIKE '%MANUAL_CODE_ENTRY_PRODUCT%' AND EXISTS (SELECT 1 FROM GALADM.GAL489TBX gal489b WHERE gal489b.COMPONENT_ID = gal489a.COMPONENT_ID AND gal489b.PROPERTY_KEY = 'TRACKING' AND UCASE(gal489b.PROPERTY_VALUE) = 'TRUE' FETCH FIRST ROW ONLY) FETCH FIRST ROW ONLY);
DELETE FROM GALADM.GAL489TBX gal489a WHERE gal489a.PROPERTY_KEY = 'TRACKING' AND EXISTS (SELECT 1 FROM GALADM.GAL489TBX gal489b WHERE gal489b.COMPONENT_ID = gal489a.COMPONENT_ID AND gal489b.PROPERTY_KEY = 'PANELS' AND gal489b.PROPERTY_VALUE LIKE '%MANUAL_CODE_ENTRY_PRODUCT%' FETCH FIRST ROW ONLY);
